import clsx from 'clsx'

export function PaymentIcon({ type, className }) {
  // https://github.com/aaronfagan/svg-credit-card-payment-icons
  className = clsx(className)
  type = type.toLowerCase()

  if (type == 'visa') return <Visa className={className} />
  if (type == 'mastercard') return <Mastercard className={className} />
  if (type == 'amex') return <Amex className={className} />
  if (type == 'google') return <GooglePlay className={className} />
  if (type == 'apple') return <ApplePay className={className} />
  if (type == 'discover') return <Discover className={className} />
  if (type == 'stripe') return <Stripe className={className} />

  if (type == 'poweredbystripe')
    return <PoweredByStripe className={className} />
}

